import React, { useRef,ref, useState } from "react";
import MyLayout from "../../MyLayout";
import {LeftOutlined,RightOutlined} from '@ant-design/icons'
import './index.css';

function HomeLayout(){

    let [backgroundImage, setbackgroundImage]=useState('https://n.sinaimg.cn/translate/20170801/EXgN-fyiphwc4556477.jpg')
    const prevB=useRef(null)
    const nextB=useRef(null)
    const mydots=useRef(null)
    const carrousel=useRef(null)
    let [currentbackImg,setCurrentBackImg]=useState(0)
    const backReserve=['https://n.sinaimg.cn/translate/20170801/EXgN-fyiphwc4556477.jpg','http://www.shanghaiaozhi.com/static/upload/image/20230609/1686300351973363.jpg','http://www.shanghaiaozhi.com/static/upload/image/20230606/1686013216275906.jpg','http://www.shanghaiaozhi.com/static/upload/image/20230606/1686013189764984.jpg']

    const clickOnNext=()=>{
        
        console.log(mydots.current.children[currentbackImg]);
        //carrousel.current.style.backgroundImage='url(https://n.sinaimg.cn/translate/20170801/EXgN-fyiphwc4556477.jpg)'
    }
    const clickOnPrev=()=>{

    }

    const overCarrousel=()=>{
        prevB.current.style.opacity=1;
        nextB.current.style.opacity=1;   
        mydots.current.style.opacity=1;   
    }
    const mouseOutOfCarrousel=()=>{
        prevB.current.style.opacity=0;
        nextB.current.style.opacity=0;  
        mydots.current.style.opacity=0;       
    }
    
    // const changeingbackground=()=>{

    // }
    // setInterval(()=>{
    //    if(currentbackImg<4){
    //     // carrousel.current.style.backgroundImage=`url('${backReserve[currentbackImg]}')`
    //     // setCurrentBackImg(currentbackImg+1)
    //     // console.log(backReserve[currentbackImg])
    //    }
    //    else{
    //     // setCurrentBackImg(0)
    //    }
    // },7000)
    return (
        <div className="homeWrapper">
           <section className="carrousel" onMouseOver={overCarrousel} onMouseOut={mouseOutOfCarrousel} ref={carrousel} >

                <div className="buttons">
                    <button className="prev" ref={prevB} onClick={clickOnPrev}><LeftOutlined /></button>
                    <button className="next" ref={nextB} onClick={clickOnNext}> <RightOutlined /></button>
                </div>
                
                <div className="carWrap">
                </div>
               
                <div className="dots" ref={mydots}>
                    <span className="dot active-dot"></span>
                    <span className="dot"></span>
                    <span className="dot"></span>
                    <span className="dot"></span>
                </div>
           </section>
           <section className="business">
            business
           </section>
           <section className="">
                dont'know yet
            </section>
        </div>
    )
}
export default function Home(){

    return (
        <>
            <MyLayout
                container={<HomeLayout/>}
            />
        </>
    )
}
